                grp_conv_2d_fu_80_ap_start_reg <= ap_const_logic_0;
            else
                if ((ap_const_logic_1 = ap_CS_fsm_state2)) then 
                    grp_conv_2d_fu_80_ap_start_reg <= ap_const_logic_1;
                elsif ((grp_conv_2d_fu_80_ap_ready = ap_const_logic_1)) then 
